import React, { useState } from "react";
import "./Home.css";

const Home: React.FC = () => {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setResponse(null);
    setError(null);

    try {
      const res = await fetch("http://localhost:4000/api/agent", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query }),
      });

      if (!res.ok) {
        throw new Error(`API error: ${res.statusText}`);
      }

      const data = await res.json();
      setResponse(data.result || "No response received.");
    } catch (err: any) {
      setError(err.message || "An error occurred.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      <div className="card">
        <h1>ü©∫ Healthcare Assistant</h1>
        <p className="subtitle">Describe your symptoms or ask a medical question</p>
        <form onSubmit={handleSubmit}>
          <textarea
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="E.g. I have a sore throat and fever..."
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? <span className="spinner" /> : "Get Advice"}
          </button>
        </form>

        {error && <p className="error">‚ö†Ô∏è {error}</p>}
        {response && (
          <div className="response-box">
            <h3>AI Response:</h3>
            <p>{response}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
