import React, { useState } from "react";
import "./Home.css";

const Home: React.FC = () => {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setResponse(null);
    setError(null);

    try {
      const res = await fetch("http://localhost:4000/api/agent", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query }),
      });

      if (!res.ok) throw new Error("Failed to fetch response from server.");
      const data = await res.json();
      setResponse(data.result || "No response.");
    } catch (err: any) {
      setError(err.message || "Unexpected error occurred.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fancy-container">
      <div className="fancy-card">
        <h1>üí¨ Healthcare Assistant</h1>
        <p className="fancy-subtitle">Ask a health question or describe symptoms below</p>
        <form onSubmit={handleSubmit} className="fancy-form">
          <textarea
            className="fancy-textarea"
            placeholder="E.g. I've had a sore throat for 3 days..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            required
          />
          <button className="fancy-button" disabled={loading}>
            {loading ? <span className="spinner" /> : "Get Help"}
          </button>
        </form>

        {error && <div className="fancy-error">‚ö†Ô∏è {error}</div>}
        {response && (
          <div className="fancy-response">
            <h3>AI Response</h3>
            <p>{response}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
